import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface HotelSetupFormProps {
  onComplete: () => void;
}

export default function HotelSetupForm({ onComplete }: HotelSetupFormProps) {
  const [formData, setFormData] = useState({
    hotelName: "",
    address: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
    phone: "",
    email: "",
    hotelType: "",
    totalRooms: "",
    description: "",
    amenities: [] as string[],
    checkInTime: "14:00",
    checkOutTime: "11:00",
    starRating: "",
    website: ""
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const availableAmenities = [
    "Free WiFi", "Swimming Pool", "Gym", "Spa", "Restaurant", 
    "Room Service", "Parking", "Business Center", "Conference Rooms",
    "Airport Shuttle", "Pet Friendly", "Laundry Service", "Concierge",
    "Bar/Lounge", "Tennis Court", "Golf Course"
  ];

  const hotelTypes = [
    "Budget Hotel", "Business Hotel", "Luxury Hotel", "Resort", 
    "Boutique Hotel", "Extended Stay", "Motel", "Bed & Breakfast", "Hostel"
  ];

  const setupMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', '/api/hotel/setup', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotel"] });
      toast({
        title: "Hotel Setup Complete!",
        description: "Your hotel details have been saved successfully.",
      });
      onComplete();
    },
    onError: (error: any) => {
      toast({
        title: "Setup Failed",
        description: error.message || "An error occurred during setup",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.hotelName || !formData.address || !formData.totalRooms) {
      toast({
        title: "Missing Required Fields",
        description: "Please fill in hotel name, address, and total rooms.",
        variant: "destructive",
      });
      return;
    }

    setupMutation.mutate(formData);
  };

  const handleAmenityChange = (amenity: string, checked: boolean) => {
    if (checked) {
      setFormData(prev => ({
        ...prev,
        amenities: [...prev.amenities, amenity]
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        amenities: prev.amenities.filter(a => a !== amenity)
      }));
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <Card className="w-full max-w-2xl">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl">Complete Your Hotel Setup</CardTitle>
          <p className="text-gray-600 mt-2">
            Please provide detailed information about your hotel to get started
          </p>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Basic Information */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">Basic Information</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="hotelName">Hotel Name *</Label>
                  <Input
                    id="hotelName"
                    value={formData.hotelName}
                    onChange={(e) => setFormData(prev => ({ ...prev, hotelName: e.target.value }))}
                    placeholder="Enter your hotel name"
                    required
                    data-testid="input-hotel-name"
                  />
                </div>

                <div>
                  <Label htmlFor="hotelType">Hotel Type</Label>
                  <Select onValueChange={(value) => setFormData(prev => ({ ...prev, hotelType: value }))}>
                    <SelectTrigger data-testid="select-hotel-type">
                      <SelectValue placeholder="Select hotel type" />
                    </SelectTrigger>
                    <SelectContent>
                      {hotelTypes.map(type => (
                        <SelectItem key={type} value={type}>{type}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="totalRooms">Total Rooms *</Label>
                  <Input
                    id="totalRooms"
                    type="number"
                    min="1"
                    value={formData.totalRooms}
                    onChange={(e) => setFormData(prev => ({ ...prev, totalRooms: e.target.value }))}
                    placeholder="Number of rooms"
                    required
                    data-testid="input-total-rooms"
                  />
                </div>

                <div>
                  <Label htmlFor="starRating">Star Rating</Label>
                  <Select onValueChange={(value) => setFormData(prev => ({ ...prev, starRating: value }))}>
                    <SelectTrigger data-testid="select-star-rating">
                      <SelectValue placeholder="Select rating" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="1">1 Star</SelectItem>
                      <SelectItem value="2">2 Star</SelectItem>
                      <SelectItem value="3">3 Star</SelectItem>
                      <SelectItem value="4">4 Star</SelectItem>
                      <SelectItem value="5">5 Star</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </div>

            {/* Contact Information */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">Contact Information</h3>

              <div>
                <Label htmlFor="address">Address *</Label>
                <Textarea
                  id="address"
                  value={formData.address}
                  onChange={(e) => setFormData(prev => ({ ...prev, address: e.target.value }))}
                  placeholder="Enter complete hotel address"
                  required
                  data-testid="input-address"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="city">City</Label>
                  <Input
                    id="city"
                    value={formData.city}
                    onChange={(e) => setFormData(prev => ({ ...prev, city: e.target.value }))}
                    placeholder="City"
                    data-testid="input-city"
                  />
                </div>

                <div>
                  <Label htmlFor="state">State</Label>
                  <Input
                    id="state"
                    value={formData.state}
                    onChange={(e) => setFormData(prev => ({ ...prev, state: e.target.value }))}
                    placeholder="State"
                    data-testid="input-state"
                  />
                </div>

                <div>
                  <Label htmlFor="pincode">Pincode</Label>
                  <Input
                    id="pincode"
                    value={formData.pincode}
                    onChange={(e) => setFormData(prev => ({ ...prev, pincode: e.target.value }))}
                    placeholder="Pincode"
                    data-testid="input-pincode"
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    id="phone"
                    type="tel"
                    value={formData.phone}
                    onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
                    placeholder="Hotel phone number"
                    data-testid="input-phone"
                  />
                </div>

                <div>
                  <Label htmlFor="email">Email Address</Label>
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="Hotel email"
                    data-testid="input-email"
                  />
                </div>
              </div>
            </div>

            {/* Hotel Policies */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">Hotel Policies</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="checkInTime">Check-in Time</Label>
                  <Input
                    id="checkInTime"
                    type="time"
                    value={formData.checkInTime}
                    onChange={(e) => setFormData(prev => ({ ...prev, checkInTime: e.target.value }))}
                    data-testid="input-checkin-time"
                  />
                </div>

                <div>
                  <Label htmlFor="checkOutTime">Check-out Time</Label>
                  <Input
                    id="checkOutTime"
                    type="time"
                    value={formData.checkOutTime}
                    onChange={(e) => setFormData(prev => ({ ...prev, checkOutTime: e.target.value }))}
                    data-testid="input-checkout-time"
                  />
                </div>
              </div>
            </div>

            {/* Amenities */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">Hotel Amenities</h3>

              <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                {availableAmenities.map(amenity => (
                  <div key={amenity} className="flex items-center space-x-2">
                    <Checkbox
                      id={amenity}
                      checked={formData.amenities.includes(amenity)}
                      onCheckedChange={(checked) => handleAmenityChange(amenity, checked as boolean)}
                      data-testid={`checkbox-${amenity.toLowerCase().replace(/\s+/g, '-')}`}
                    />
                    <Label htmlFor={amenity} className="text-sm">{amenity}</Label>
                  </div>
                ))}
              </div>
            </div>

            {/* Description */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">Additional Information</h3>

              <div>
                <Label htmlFor="description">Hotel Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Describe your hotel, unique features, and what makes it special..."
                  rows={3}
                  data-testid="input-description"
                />
              </div>

              <div>
                <Label htmlFor="website">Website (Optional)</Label>
                <Input
                  id="website"
                  type="url"
                  value={formData.website}
                  onChange={(e) => setFormData(prev => ({ ...prev, website: e.target.value }))}
                  placeholder="https://yourhotel.com"
                  data-testid="input-website"
                />
              </div>
            </div>

            <Button
              type="submit"
              className="w-full"
              disabled={setupMutation.isPending}
              data-testid="button-complete-setup"
            >
              {setupMutation.isPending ? "Saving Hotel Details..." : "Complete Hotel Setup"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/LoginForm.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import RegistrationForm from "./RegistrationForm";

export function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showRegistration, setShowRegistration] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const loginMutation = useMutation({
    mutationFn: async (credentials: { username: string; password: string }) => {
      const response = await apiRequest('POST', '/api/login', credentials);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Login successful",
        description: `Welcome to ${data.user?.hotelName || 'your hotel'} dashboard!`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Login failed",
        description: error.message || "Invalid credentials",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    loginMutation.mutate({ username, password });
  };

  const handleRegistrationSuccess = () => {
    setShowRegistration(false);
    queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
  };

  if (showRegistration) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <RegistrationForm
          onSuccess={handleRegistrationSuccess}
          onSwitchToLogin={() => setShowRegistration(false)}
        />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl text-center">Sign in to your dashboard</CardTitle>
          <p className="text-sm text-gray-600 text-center">
            Enter your credentials to access the hotel management system
          </p>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter username"
                required
                data-testid="input-username"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter password"
                required
                data-testid="input-password"
              />
            </div>
            <Button
              type="submit"
              className="w-full"
              disabled={loginMutation.isPending}
              data-testid="button-login"
            >
              {loginMutation.isPending ? "Signing in..." : "Sign in"}
            </Button>
          </form>

          <div className="mt-4 text-center">
            <Button
              type="button"
              variant="link"
              onClick={() => setShowRegistration(true)}
              data-testid="button-switch-register"
            >
              Don't have an account? Create one for your hotel
            </Button>
          </div>


        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/RegistrationForm.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertHotelAdminSchema } from "@shared/types";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

type FormData = z.infer<typeof insertHotelAdminSchema>;

interface RegistrationFormProps {
  onSuccess: () => void;
  onSwitchToLogin: () => void;
}

export default function RegistrationForm({ onSuccess, onSwitchToLogin }: RegistrationFormProps) {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm<FormData>({
    resolver: zodResolver(insertHotelAdminSchema),
  });

  const onSubmit = async (data: FormData) => {
    setIsLoading(true);
    try {
      const response = await apiRequest("POST", "/api/register", data);
      const result = await response.json();

      if (result.success) {
        toast({
          title: "Account Created Successfully!",
          description: "You can now login with your credentials.",
        });
        // Go back to login page after successful registration
        onSwitchToLogin();
      } else {
        throw new Error(result.message || "Registration failed");
      }
    } catch (error: any) {
      toast({
        title: "Registration Failed",
        description: error.message || "An error occurred during registration",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader className="text-center">
        <CardTitle>Create Hotel Account</CardTitle>
        <CardDescription>
          Register your hotel to start managing customers and services
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          <div>
            <Label htmlFor="hotelName">Hotel Name</Label>
            <Input
              id="hotelName"
              {...register("hotelName")}
              placeholder="Enter your hotel name"
              data-testid="input-hotel-name"
            />
            {errors.hotelName && (
              <p className="text-sm text-red-500 mt-1">{errors.hotelName.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="username">Username</Label>
            <Input
              id="username"
              {...register("username")}
              placeholder="Choose a username"
              data-testid="input-username"
            />
            {errors.username && (
              <p className="text-sm text-red-500 mt-1">{errors.username.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="password">Password</Label>
            <Input
              id="password"
              type="text"
              {...register("password")}
              placeholder="Create a password"
              data-testid="input-password"
            />
            {errors.password && (
              <p className="text-sm text-red-500 mt-1">{errors.password.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="email">Email Address</Label>
            <Input
              id="email"
              type="email"
              {...register("email")}
              placeholder="Enter your email"
              data-testid="input-email"
            />
            {errors.email && (
              <p className="text-sm text-red-500 mt-1">{errors.email.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="phone">Phone Number (Optional)</Label>
            <Input
              id="phone"
              type="tel"
              {...register("phone")}
              placeholder="Enter phone number"
              data-testid="input-phone"
            />
            {errors.phone && (
              <p className="text-sm text-red-500 mt-1">{errors.phone.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="address">Address (Optional)</Label>
            <Input
              id="address"
              {...register("address")}
              placeholder="Enter hotel address"
              data-testid="input-address"
            />
            {errors.address && (
              <p className="text-sm text-red-500 mt-1">{errors.address.message}</p>
            )}
          </div>

          <Button
            type="submit"
            className="w-full"
            disabled={isLoading}
            data-testid="button-register"
          >
            {isLoading ? "Creating Account..." : "Create Hotel Account"}
          </Button>

          <div className="text-center">
            <Button
              type="button"
              variant="link"
              onClick={onSwitchToLogin}
              data-testid="button-switch-login"
            >
              Already have an account? Sign in
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/add-customer-modal.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { insertCustomerSchema, RoomType, Customer } from "@shared/types";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CheckCircle, QrCode, User, Phone, Mail, Bed, Calendar, Printer } from "lucide-react";
import { format } from "date-fns";

const formSchema = insertCustomerSchema.omit({ hotelId: true, roomTypeName: true, roomPrice: true }).extend({
  checkinTime: z.string().optional(),
});

type FormData = z.infer<typeof formSchema>;

interface AddCustomerModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function AddCustomerModal({ open, onOpenChange }: AddCustomerModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [successData, setSuccessData] = useState<Customer | null>(null);
  const [showSuccessPage, setShowSuccessPage] = useState(false);

  // Fetch room types
  const { data: roomTypes = [] } = useQuery<RoomType[]>({
    queryKey: ["/api/room-types"],
  });

  const { data: availableRooms = {} } = useQuery<{ [roomTypeId: string]: string[] }>({
    queryKey: ["/api/available-rooms"],
  });

  const {
    register,
    handleSubmit,
    reset,
    watch,
    setValue,
    formState: { errors },
  } = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      checkinTime: new Date(new Date().getTime() + (5.5 * 60 * 60 * 1000)).toISOString().slice(0, 16),
      isActive: true,
      name: '',
      phone: '',
      email: '',
      roomTypeId: '',
      roomNumber: '',
      expectedStayDays: 1,
    },
  });

  const createCustomerMutation = useMutation({
    mutationFn: async (data: FormData) => {
      // Find the selected room type to get pricing info
      const selectedRoomType = roomTypes.find((rt: RoomType) => rt.id === data.roomTypeId);

      if (!selectedRoomType) {
        throw new Error("Please select a valid room type");
      }

      const customerData = {
        ...data,
        roomTypeName: selectedRoomType.name,
        roomPrice: selectedRoomType.price,
        checkinTime: data.checkinTime ? new Date(data.checkinTime) : new Date(new Date().getTime() + (5.5 * 60 * 60 * 1000)),
      };

      const response = await apiRequest("POST", "/api/customers", customerData);
      const result = await response.json();
      return result;
    },
    onSuccess: (data: Customer) => {
      setSuccessData(data);
      setShowSuccessPage(true);
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/room-types"] });
      queryClient.invalidateQueries({ queryKey: ["/api/available-rooms"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add customer",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FormData) => {
    // Basic validation
    if (!data.name || data.name.trim() === '') {
      toast({
        title: "Validation Error",
        description: "Customer name is required",
        variant: "destructive",
      });
      return;
    }

    if (!data.phone || data.phone.trim() === '') {
      toast({
        title: "Validation Error",
        description: "Phone number is required",
        variant: "destructive",
      });
      return;
    }

    // Enhanced phone number validation
    const phoneRegex = /^[+]?[1-9]\d{1,14}$/;
    if (!phoneRegex.test(data.phone.replace(/\s/g, ''))) {
      toast({
        title: "Invalid Phone Number",
        description: "Please enter a valid phone number with country code (e.g., +91 9876543210)",
        variant: "destructive",
      });
      return;
    }

    // Email validation (if provided)
    if (data.email && data.email.trim() !== '') {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(data.email)) {
        toast({
          title: "Invalid Email",
          description: "Please enter a valid email address",
          variant: "destructive",
        });
        return;
      }
    }

    if (!data.roomTypeId) {
      toast({
        title: "Validation Error",
        description: "Please select a room type",
        variant: "destructive",
      });
      return;
    }

    if (!data.roomNumber) {
      toast({
        title: "Validation Error",
        description: "Please select a room number",
        variant: "destructive",
      });
      return;
    }

    createCustomerMutation.mutate(data);
  };

  const handleCloseModal = () => {
    setShowSuccessPage(false);
    setSuccessData(null);
    onOpenChange(false);
    reset();
  };

  const handlePrintQR = () => {
    if (successData?.qrCode) {
      const printWindow = window.open('', '_blank');
      if (printWindow) {
        printWindow.document.write(`
          <html>
            <head>
              <title>QR Code - Room ${successData.roomNumber}</title>
              <style>
                body { text-align: center; font-family: Arial, sans-serif; margin: 40px; }
                .qr-container { border: 2px solid #ccc; padding: 20px; display: inline-block; }
                h2 { margin-bottom: 20px; }
                .guest-info { margin: 20px 0; }
              </style>
            </head>
            <body>
              <h2>Hotel Service QR Code</h2>
              <div class="guest-info">
                <p><strong>Guest:</strong> ${successData.name}</p>
                <p><strong>Room:</strong> ${successData.roomNumber}</p>
                <p><strong>Room Type:</strong> ${successData.roomTypeName}</p>
              </div>
              <div class="qr-container">
                <img src="${successData.qrCode}" alt="QR Code" style="max-width: 200px; height: auto;" />
              </div>
              <p style="margin-top: 20px; font-size: 12px; color: #666;">
                Scan this QR code to access hotel services
              </p>
            </body>
          </html>
        `);
        printWindow.document.close();
        printWindow.print();
      }
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleCloseModal}>
      <DialogContent className="w-[95vw] max-w-md sm:max-w-lg md:max-w-xl lg:max-w-2xl mx-auto max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            {showSuccessPage ? "Guest Added Successfully!" : "Add New Customer"}
          </DialogTitle>
        </DialogHeader>

        {showSuccessPage && successData ? (
          <div className="space-y-6 p-1">
            {/* Success Header */}
            <div className="flex items-center justify-center space-x-3 py-4">
              <CheckCircle className="w-12 h-12 text-green-500" />
              <div className="text-center">
                <h3 className="text-lg font-semibold text-green-700">Guest Added Successfully!</h3>
                <p className="text-sm text-gray-600">QR code generated and ready for use</p>
              </div>
            </div>

            {/* Guest Details */}
            <Card className="border-green-200">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <User className="w-5 h-5" />
                  <span>Guest Details</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="flex items-center space-x-2">
                    <User className="w-4 h-4 text-gray-400" />
                    <span className="font-medium">{successData.name}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Bed className="w-4 h-4 text-gray-400" />
                    <span>Room {successData.roomNumber}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Phone className="w-4 h-4 text-gray-400" />
                    <span>{successData.phone}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant="secondary">{successData.roomTypeName}</Badge>
                  </div>
                  {successData.email && (
                    <div className="flex items-center space-x-2 md:col-span-2">
                      <Mail className="w-4 h-4 text-gray-400" />
                      <span>{successData.email}</span>
                    </div>
                  )}
                  <div className="flex items-center space-x-2 md:col-span-2">
                    <Calendar className="w-4 h-4 text-gray-400" />
                    <span>Check-in: {format(new Date(successData.checkinTime!), "MMM dd, yyyy 'at' h:mm a")}</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* QR Code Section */}
            <Card className="border-blue-200">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <QrCode className="w-5 h-5" />
                  <span>Hotel Service QR Code</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="text-center space-y-4">
                {successData.qrCode && (
                  <div className="flex flex-col items-center space-y-4">
                    <div className="border-2 border-gray-200 rounded-lg p-4 bg-white">
                      <img 
                        src={successData.qrCode} 
                        alt="QR Code for Hotel Services" 
                        className="max-w-[200px] h-auto mx-auto"
                      />
                    </div>
                    <div className="text-sm text-gray-600 max-w-md">
                      <p className="font-medium mb-2">Instructions for Guest:</p>
                      <ul className="text-left space-y-1">
                        <li>• Scan this QR code with your phone camera</li>
                        <li>• Access hotel services instantly</li>
                        <li>• Request room service, maintenance, or concierge</li>
                      </ul>
                    </div>
                    <Button 
                      onClick={handlePrintQR} 
                      variant="outline" 
                      className="flex items-center space-x-2"
                    >
                      <Printer className="w-4 h-4" />
                      <span>Print QR Code</span>
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4 pt-4">
              <Button
                type="button"
                variant="outline"
                className="flex-1"
                onClick={() => {
                  setShowSuccessPage(false);
                  setSuccessData(null);
                  reset();
                }}
              >
                Add Another Guest
              </Button>
              <Button
                type="button"
                className="flex-1 bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 text-white"
                onClick={handleCloseModal}
              >
                Done
              </Button>
            </div>
          </div>
        ) : (
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4 p-1">
          <div>
            <Label htmlFor="name">Customer Name</Label>
            <Input
              id="name"
              {...register("name")}
              placeholder="Enter customer name"
              data-testid="input-customer-name"
            />
            {errors.name && (
              <p className="text-sm text-red-500 mt-1">{errors.name.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="phone">Phone Number</Label>
            <Input
              id="phone"
              type="tel"
              {...register("phone")}
              placeholder="Enter phone number with country code (e.g., +91 9876543210)"
              data-testid="input-customer-phone"
            />
            <p className="text-xs text-gray-500 mt-1">
              Include country code (e.g., +91 for India, +1 for US/Canada, +44 for UK)
            </p>
            {errors.phone && (
              <p className="text-sm text-red-500 mt-1">{errors.phone.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="email">Email Address</Label>
            <Input
              id="email"
              type="email"
              {...register("email")}
              placeholder="Enter valid email address (e.g., guest@example.com)"
              data-testid="input-customer-email"
            />
            <p className="text-xs text-gray-500 mt-1">
              Optional - Valid email format required if provided
            </p>
            {errors.email && (
              <p className="text-sm text-red-500 mt-1">{errors.email.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="roomTypeId">Room Type</Label>
            <Select
              value={watch("roomTypeId") || ""}
              onValueChange={(value) => {
                setValue("roomTypeId", value, { shouldValidate: true });
                setValue("roomNumber", "", { shouldValidate: true }); // Reset room number when room type changes
              }}
            >
              <SelectTrigger data-testid="select-room-type">
                <SelectValue placeholder="Select room type" />
              </SelectTrigger>
              <SelectContent>
                {roomTypes.map((roomType: RoomType) => {
                  const availableCount = availableRooms[roomType.id]?.length || 0;
                  return (
                    <SelectItem 
                      key={roomType.id} 
                      value={roomType.id}
                      disabled={availableCount === 0}
                    >
                      <div className="flex flex-col">
                        <span>{roomType.name}</span>
                        <span className="text-xs text-gray-500">
                          ₹{roomType.price}/night • {availableCount} available
                        </span>
                      </div>
                    </SelectItem>
                  );
                })}
              </SelectContent>
            </Select>
            {errors.roomTypeId && (
              <p className="text-sm text-red-500 mt-1">{errors.roomTypeId.message}</p>
            )}
          </div>

          {watch("roomTypeId") && (
            <div>
              <Label htmlFor="roomNumber">Available Room Numbers</Label>
              <Select
                value={watch("roomNumber") || ""}
                onValueChange={(value) => setValue("roomNumber", value, { shouldValidate: true })}
              >
                <SelectTrigger data-testid="select-room-number">
                  <SelectValue placeholder="Select room number" />
                </SelectTrigger>
                <SelectContent>
                  {(availableRooms[watch("roomTypeId")] || []).map((roomNumber: string) => (
                    <SelectItem key={roomNumber} value={roomNumber}>
                      Room {roomNumber}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.roomNumber && (
                <p className="text-sm text-red-500 mt-1">{errors.roomNumber.message}</p>
              )}
            </div>
          )}

          <div>
            <Label htmlFor="expectedStayDays">Expected Stay (days)</Label>
            <Input
              id="expectedStayDays"
              type="number"
              {...register("expectedStayDays", { valueAsNumber: true })}
              placeholder="Days"
              data-testid="input-expected-stay"
            />
            {errors.expectedStayDays && (
              <p className="text-sm text-red-500 mt-1">{errors.expectedStayDays.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="checkinTime">Check-in Date & Time</Label>
            <Input
              id="checkinTime"
              type="datetime-local"
              {...register("checkinTime")}
              data-testid="input-checkin-time"
            />
            {errors.checkinTime && (
              <p className="text-sm text-red-500 mt-1">{errors.checkinTime.message}</p>
            )}
          </div>

          <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4 pt-4">
            <Button
              type="button"
              variant="outline"
              className="flex-1"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              className="flex-1 bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white"
              disabled={createCustomerMutation.isPending}
              data-testid="button-submit"
            >
              {createCustomerMutation.isPending ? "Adding..." : "Add Customer"}
            </Button>
          </div>
        </form>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/assign-service-mod.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { ServiceRequest } from "@shared/types";

interface AssignServiceModalProps {
  request: ServiceRequest;
  isOpen: boolean;
  onClose: () => void;
}

export default function AssignServiceModal({ request, isOpen, onClose }: AssignServiceModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [assignedTo, setAssignedTo] = useState("");
  const [timeFrame, setTimeFrame] = useState("");

  const assignMutation = useMutation({
    mutationFn: async (data: { assignedTo: string; timeFrame: string }) => {
      // Create admin service record
      const adminServiceResponse = await apiRequest("POST", "/api/admin-services", {
        serviceRequestId: request.id,
        requestType: request.service || request.type || "Service Request",
        assignedTo: data.assignedTo,
        timeFrame: data.timeFrame,
        hotelId: request.hotelId,
        service: true
      });

      // Update service request status
      const updateResponse = await apiRequest("PUT", `/api/service-requests/${request.id}`, {
        status: "assigned",
        assignedTo: data.assignedTo,
        assignedBy: "admin",
        assignedAt: new Date(),
      });

      return { adminService: await adminServiceResponse.json(), serviceRequest: await updateResponse.json() };
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/service-requests"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin-services"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
      toast({
        title: "Service Request Assigned",
        description: `Assigned to ${assignedTo} for ${timeFrame}`,
      });
      setAssignedTo("");
      setTimeFrame("");
      onClose();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to assign service request",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!assignedTo.trim() || !timeFrame.trim()) {
      toast({
        title: "Error",
        description: "Please fill in all fields",
        variant: "destructive",
      });
      return;
    }
    assignMutation.mutate({ assignedTo: assignedTo.trim(), timeFrame: timeFrame.trim() });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Assign Service Request</DialogTitle>
          <DialogDescription>
            Assign this service request to a staff member with a timeframe.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="requestType">Service Request</Label>
            <Input
              id="requestType"
              value={request.service || request.type || "Service Request"}
              disabled
              className="bg-gray-50"
              data-testid="input-request-type"
            />
          </div>

          <div>
            <Label htmlFor="customerName">Customer Name</Label>
            <Input
              id="customerName"
              value={request.guestName || "Not specified"}
              disabled
              className="bg-gray-50"
              data-testid="input-customer-name"
            />
          </div>

          <div>
            <Label htmlFor="roomNumber">Room Number</Label>
            <Input
              id="roomNumber"
              value={request.roomNumber}
              disabled
              className="bg-gray-50"
              data-testid="input-room-number"
            />
          </div>

          <div>
            <Label htmlFor="assignedTo">Assign to Person</Label>
            <Input
              id="assignedTo"
              value={assignedTo}
              onChange={(e) => setAssignedTo(e.target.value)}
              placeholder="Enter staff member name"
              required
              data-testid="input-assigned-to"
            />
          </div>

          <div>
            <Label htmlFor="timeFrame">Time Frame</Label>
            <Input
              id="timeFrame"
              value={timeFrame}
              onChange={(e) => setTimeFrame(e.target.value)}
              placeholder="e.g., 30 minutes, 2 hours, by 3 PM"
              required
              data-testid="input-time-frame"
            />
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={assignMutation.isPending}
              data-testid="button-submit"
            >
              {assignMutation.isPending ? "Assigning..." : "Assign"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/room-management-mo.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Plus, QrCode } from "lucide-react";
import { RoomType } from "@shared/types";

interface RoomManagementModalProps {
  trigger?: React.ReactNode;
}

export default function RoomManagementModal({ trigger }: RoomManagementModalProps) {
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState({
    roomNumber: "",
    roomTypeId: "",
    roomTypeName: "",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: roomTypes = [] } = useQuery<RoomType[]>({
    queryKey: ["/api/room-types"],
  });

  const generateRoomQRMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const response = await fetch("/api/generate-room-qr", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include", // Include cookies for authentication
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        let errorMessage = "Failed to generate room QR code";
        try {
          const error = await response.json();
          errorMessage = error.message || errorMessage;
        } catch {
          errorMessage = `HTTP ${response.status}: ${response.statusText}`;
        }
        throw new Error(errorMessage);
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Room QR code generated successfully",
      });
      setOpen(false);
      setFormData({ roomNumber: "", roomTypeId: "", roomTypeName: "" });
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.roomNumber || !formData.roomTypeId || !formData.roomTypeName) {
      toast({
        title: "Error",
        description: "Please fill in all fields",
        variant: "destructive",
      });
      return;
    }

    generateRoomQRMutation.mutate(formData);
  };

  const handleRoomTypeChange = (roomTypeId: string) => {
    const selectedRoomType = roomTypes.find(rt => rt.id === roomTypeId);
    setFormData({
      ...formData,
      roomTypeId,
      roomTypeName: selectedRoomType?.name || "",
    });
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button className="flex items-center space-x-2">
            <QrCode className="w-4 h-4" />
            <span>Generate Room QR Code</span>
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <QrCode className="w-5 h-5" />
            <span>Generate Room QR Code</span>
          </DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="roomNumber">Room Number</Label>
            <Input
              id="roomNumber"
              value={formData.roomNumber}
              onChange={(e) => setFormData({ ...formData, roomNumber: e.target.value })}
              placeholder="Enter room number (e.g., 101, 205)"
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="roomType">Room Type</Label>
            <Select value={formData.roomTypeId} onValueChange={handleRoomTypeChange} required>
              <SelectTrigger>
                <SelectValue placeholder="Select a room type" />
              </SelectTrigger>
              <SelectContent>
                {roomTypes.map((roomType) => (
                  <SelectItem key={roomType.id} value={roomType.id}>
                    {roomType.name} - ₹{roomType.price}/night
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={() => setOpen(false)}
              disabled={generateRoomQRMutation.isPending}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={generateRoomQRMutation.isPending}
              className="flex items-center space-x-2"
            >
              {generateRoomQRMutation.isPending ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                  <span>Generating...</span>
                </>
              ) : (
                <>
                  <Plus className="w-4 h-4" />
                  <span>Generate QR Code</span>
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/room-qr-display.ts.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Download, QrCode, ExternalLink, RefreshCw } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface RoomQRCode {
  roomNumber: string;
  roomType: string;
  qrCode: string;
  qrCodeUrl: string;
}

interface RoomQRDisplayProps {
  hotelId?: string;
}

export default function RoomQRDisplay({ hotelId }: RoomQRDisplayProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: roomQRCodes = [], isLoading } = useQuery<RoomQRCode[]>({
    queryKey: ["/api/qr-codes", hotelId],
    queryFn: async () => {
      if (!hotelId) return [];
      const response = await fetch(`/api/qr-codes/${hotelId}`);
      if (!response.ok) throw new Error('Failed to fetch room QR codes');
      return response.json();
    },
    enabled: !!hotelId,
  });

  const regenerateQRMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/regenerate-all-qr-codes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Include cookies for authentication
      });

      if (!response.ok) {
        let errorMessage = 'Failed to regenerate QR codes';
        try {
          const error = await response.json();
          errorMessage = error.message || errorMessage;
        } catch {
          errorMessage = `HTTP ${response.status}: ${response.statusText}`;
        }
        throw new Error(errorMessage);
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Success",
        description: `Regenerated QR codes for ${data.updatedCount} rooms to point to service app`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/qr-codes", hotelId] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const downloadQRCode = (roomNumber: string, qrCode: string) => {
    try {
      const link = document.createElement('a');
      link.href = qrCode;
      link.download = `room-${roomNumber}-qr-code.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      toast({
        title: "Success",
        description: `QR code for Room ${roomNumber} downloaded`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to download QR code",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {Array.from({ length: 6 }).map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader>
              <div className="h-6 bg-gray-200 rounded" />
            </CardHeader>
            <CardContent>
              <div className="w-full h-48 bg-gray-200 rounded mb-4" />
              <div className="h-4 bg-gray-200 rounded mb-2" />
              <div className="h-8 bg-gray-200 rounded" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (roomQRCodes.length === 0) {
    return (
      <Card className="border-0 shadow-lg bg-gradient-to-br from-white to-gray-50/30">
        <CardContent className="flex flex-col items-center justify-center py-16">
          <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-purple-600 rounded-2xl flex items-center justify-center mb-6">
            <QrCode className="h-8 w-8 text-white" />
          </div>
          <h3 className="text-xl font-semibold mb-3 text-gray-800">No Room QR Codes Generated</h3>
          <p className="text-gray-600 text-center max-w-md">
            Generate QR codes for your rooms so guests can easily access hotel services.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-gray-800">Room QR Codes</h3>
        <Button
          onClick={() => regenerateQRMutation.mutate()}
          disabled={regenerateQRMutation.isPending}
          className="flex items-center space-x-2"
          variant="outline"
        >
          <RefreshCw className={`w-4 h-4 ${regenerateQRMutation.isPending ? 'animate-spin' : ''}`} />
          <span>{regenerateQRMutation.isPending ? 'Updating...' : 'Update All QR Codes'}</span>
        </Button>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
      {roomQRCodes.map((room) => (
        <Card key={room.roomNumber} className="border-0 shadow-lg bg-gradient-to-br from-white to-purple-50/30 hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center space-x-2">
                <QrCode className="w-5 h-5 text-purple-600" />
                <span>Room {room.roomNumber}</span>
              </CardTitle>
              <Badge variant="outline" className="bg-purple-50 text-purple-700">
                {room.roomType}
              </Badge>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex justify-center p-4 bg-white rounded-lg shadow-inner">
              <img 
                src={room.qrCode} 
                alt={`QR Code for Room ${room.roomNumber}`}
                className="w-48 h-48 object-contain"
              />
            </div>

            <div className="space-y-2">
              <p className="text-sm text-gray-600 break-all">
                <strong>URL:</strong> {room.qrCodeUrl}
              </p>
            </div>

            <div className="flex space-x-2">
              <Button
                size="sm"
                variant="outline"
                className="flex-1 flex items-center space-x-2"
                onClick={() => downloadQRCode(room.roomNumber, room.qrCode)}
              >
                <Download className="w-4 h-4" />
                <span>Download</span>
              </Button>
              <Button
                size="sm"
                variant="outline"
                className="flex-1 flex items-center space-x-2"
                onClick={() => window.open(room.qrCodeUrl, '_blank')}
              >
                <ExternalLink className="w-4 h-4" />
                <span>Test</span>
              </Button>
            </div>
          </CardContent>
        </Card>
      ))}
      </div>
    </div>
  );
}

client/src/components/service-request-ca.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Wrench, 
  Users, 
  Utensils, 
  Sparkles, 
  HeadphonesIcon, 
  MoreHorizontal 
} from "lucide-react";
import { ServiceRequest } from "@shared/types";
import { formatDistanceToNow } from "date-fns";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import AssignServiceModal from "./assign-service-modal";

interface ServiceRequestCardProps {
  request: ServiceRequest;
}

const getRequestIcon = (type: string) => {
  switch (type) {
    case "maintenance":
      return Wrench;
    case "room_service":
      return Users;
    case "food_delivery":
      return Utensils;
    case "housekeeping":
      return Sparkles;
    case "concierge":
      return HeadphonesIcon;
    default:
      return MoreHorizontal;
  }
};

const getRequestIconColor = (type: string) => {
  switch (type) {
    case "maintenance":
      return "text-amber-600 bg-amber-100";
    case "room_service":
      return "text-blue-600 bg-blue-100";
    case "food_delivery":
      return "text-green-600 bg-green-100";
    case "housekeeping":
      return "text-purple-600 bg-purple-100";
    case "concierge":
      return "text-pink-600 bg-pink-100";
    default:
      return "text-gray-600 bg-gray-100";
  }
};

const getStatusColor = (status: string) => {
  switch (status) {
    case "pending":
      return "bg-yellow-100 text-yellow-800";
    case "assigned":
      return "bg-blue-100 text-blue-800";
    case "in_progress":
      return "bg-purple-100 text-purple-800";
    case "completed":
      return "bg-green-100 text-green-800";
    case "cancelled":
      return "bg-red-100 text-red-800";
    default:
      return "bg-gray-100 text-gray-800";
  }
};

export default function ServiceRequestCard({ request }: ServiceRequestCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showAssignModal, setShowAssignModal] = useState(false);
  const Icon = getRequestIcon(request.type || 'other');

  const updateRequestMutation = useMutation({
    mutationFn: async (data: { status?: string; assignedTo?: string; assignedBy?: string; completedBy?: string; assignedAt?: Date; completedAt?: Date }) => {
      const response = await apiRequest("PUT", `/api/service-requests/${request.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/service-requests"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update service request",
        variant: "destructive",
      });
    },
  });

  const handleAssign = () => {
    setShowAssignModal(true);
  };

  const completeRequestMutation = useMutation({
    mutationFn: async () => {
      // Update service request to completed
      const serviceResponse = await apiRequest("PUT", `/api/service-requests/${request.id}`, {
        status: "completed",
        completedBy: request.assignedTo || "admin",
        completedAt: new Date(),
      });

      // Try to update admin service to mark service: false (completion)
      // This may fail if no admin service exists, which is okay
      try {
        const adminResponse = await apiRequest("PUT", `/api/admin-services/${request.id}`, {
          service: false,
          completedAt: new Date(),
        });
      } catch (error) {
        console.log("No admin service found for completion, continuing...");
      }

      return { serviceResponse };
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/service-requests"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin-services"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
      toast({
        title: "Service Request Completed",
        description: "Request marked as completed",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete service request",
        variant: "destructive",
      });
    },
  });

  const handleComplete = () => {
    completeRequestMutation.mutate();
  };

  return (
    <Card className="hover:border-gray-300 transition-colors">
      <CardContent className="p-4">
        <div className="flex items-start space-x-4">
          <div className={`w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0 ${getRequestIconColor(request.type || 'other')}`}>
            <Icon className="w-5 h-5" />
          </div>

          <div className="flex-1 min-w-0">
            <div className="flex items-center justify-between mb-2">
              <h4 
                className="text-sm font-semibold text-gray-900 capitalize"
                data-testid={`text-request-type-${request.id}`}
              >
                {request.service || request.type?.replace('_', ' ') || 'Service Request'}
              </h4>
              <div className="flex items-center space-x-2">
                <Badge 
                  variant="secondary" 
                  className={getStatusColor(request.status)}
                  data-testid={`badge-status-${request.id}`}
                >
                  {request.status}
                </Badge>
                <span 
                  className="text-xs text-gray-500"
                  data-testid={`text-request-time-${request.id}`}
                >
                  {formatDistanceToNow(new Date(request.requestedAt), { addSuffix: true })}
                </span>
              </div>
            </div>

            <p 
              className="text-sm text-gray-600 mb-3"
              data-testid={`text-request-description-${request.id}`}
            >
              {request.notes || request.description || 'No details provided'}
            </p>

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2 text-xs text-gray-500">
                <span>Room:</span>
                <span 
                  className="font-medium"
                  data-testid={`text-room-number-${request.id}`}
                >
                  {request.roomNumber}
                </span>
                {request.guestName && (
                  <>
                    <span>•</span>
                    <span data-testid={`text-guest-name-${request.id}`}>
                      Guest: {request.guestName}
                    </span>
                  </>
                )}
                {request.assignedTo && (
                  <>
                    <span>•</span>
                    <span data-testid={`text-assigned-to-${request.id}`}>
                      Assigned to {request.assignedTo}
                    </span>
                  </>
                )}
              </div>

              <div className="flex space-x-2">
                {request.status === "pending" && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleAssign}
                    disabled={updateRequestMutation.isPending}
                    className="text-blue-500 hover:text-blue-600 text-xs"
                    data-testid={`button-assign-${request.id}`}
                  >
                    Assign
                  </Button>
                )}

                {(request.status === "pending" || request.status === "assigned" || request.status === "in_progress") && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleComplete}
                    disabled={completeRequestMutation.isPending}
                    className="text-green-500 hover:text-green-600 text-xs"
                    data-testid={`button-complete-${request.id}`}
                  >
                    {completeRequestMutation.isPending ? "Completing..." : "Complete"}
                  </Button>
                )}
              </div>
            </div>
          </div>
        </div>
      </CardContent>

      <AssignServiceModal
        request={request}
        isOpen={showAssignModal}
        onClose={() => setShowAssignModal(false)}
      />
    </Card>
  );
}

client/src/components/sidebar.tsx.content:
import { Link, useLocation } from "wouter";
import { cn } from "@/lib/utils";
import { useAuth } from "@/hooks/useAuth";
import { User as UserType } from "@shared/types";
import { 
  LayoutDashboard, 
  Users, 
  Bed, 
  Bell, 
  BarChart3, 
  FileText, 
  Hotel, 
  LogOut,
  User
