import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Bed, Users, Crown, Home, RefreshCw, QrCode } from "lucide-react";
import { RoomType } from "@shared/types";
import { useToast } from "@/hooks/use-toast";
import RoomManagementModal from "@/components/room-management-modal";
import RoomQRDisplay from "@/components/room-qr-display";
import { useAuth } from "@/hooks/useAuth";

export default function Rooms() {
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();

  const { data: roomTypes = [], isLoading, isFetching } = useQuery<RoomType[]>({
    queryKey: ["/api/room-types"],
  });

  const { data: hotel } = useQuery<{ id: string }>({
    queryKey: ["/api/hotel"],
    enabled: !!user,
  });

  const handleRefresh = async () => {
    try {
      // First recalculate room availability based on actual active customers
      const response = await fetch('/api/recalculate-rooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to recalculate room availability');
      }

      // Then invalidate all room-related queries to fetch fresh data
      await queryClient.invalidateQueries({ queryKey: ["/api/room-types"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/available-rooms"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });

      toast({
        title: "Refreshed",
        description: "Room availability updated from database",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to refresh room data",
        variant: "destructive",
      });
    }
  };

  const getRoomIcon = (type: string) => {
    switch (type) {
      case 'single':
        return <Bed className="h-5 w-5" />;
      case 'double':
      case 'twin':
      case 'triple':
        return <Users className="h-5 w-5" />;
      case 'junior_suite':
      case 'executive_suite':
      case 'presidential_suite':
        return <Crown className="h-5 w-5" />;
      default:
        return <Home className="h-5 w-5" />;
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'standard':
        return 'bg-blue-100 text-blue-800';
      case 'deluxe':
        return 'bg-purple-100 text-purple-800';
      case 'suite':
        return 'bg-gold-100 text-gold-800';
      case 'studio':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getAvailabilityColor = (available: number, total: number) => {
    const percentage = (available / total) * 100;
    if (percentage === 0) return 'bg-red-100 text-red-800';
    if (percentage <= 20) return 'bg-orange-100 text-orange-800';
    if (percentage <= 50) return 'bg-yellow-100 text-yellow-800';
    return 'bg-green-100 text-green-800';
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-r from-white to-purple-50/50 shadow-sm border-b border-gray-100 px-6 py-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent">Room Management</h2>
            <p className="text-sm text-gray-600 mt-1">Monitor room availability and types across your hotel</p>
          </div>
          <Button 
            onClick={handleRefresh}
            disabled={isFetching}
            className="flex items-center space-x-2"
            data-testid="button-refresh-rooms"
          >
            <RefreshCw className={`w-4 h-4 ${isFetching ? 'animate-spin' : ''}`} />
            <span>{isFetching ? 'Refreshing...' : 'Refresh'}</span>
          </Button>
        </div>
      </header>

      {/* Content */}
      <main className="flex-1 overflow-y-auto p-6 bg-gradient-to-br from-gray-50/30 to-purple-50/20">
        <Tabs defaultValue="room-types" className="space-y-6">
          <div className="flex items-center justify-between">
            <TabsList>
              <TabsTrigger value="room-types">Room Types</TabsTrigger>
              <TabsTrigger value="qr-codes">QR Codes</TabsTrigger>
            </TabsList>
            <RoomManagementModal />
          </div>

          <TabsContent value="room-types">
            <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
              {roomTypes.map((roomType) => (
          <Card key={roomType.id} className="border-0 shadow-lg bg-gradient-to-br from-white to-purple-50/30 hover:shadow-xl transition-all duration-300 overflow-hidden">
            <CardHeader className="pb-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-purple-600 rounded-lg flex items-center justify-center">
                    {getRoomIcon(roomType.type)}
                  </div>
                  <CardTitle className="text-xl font-semibold text-gray-800">{roomType.name}</CardTitle>
                </div>
                <Badge className={getCategoryColor(roomType.category)}>
                  {roomType.category}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="flex items-center justify-between p-4 bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl">
                <span className="text-3xl font-bold bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">₹{roomType.price.toLocaleString()}</span>
                <span className="text-sm font-medium text-gray-600">per night</span>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium">Availability</span>
                  <Badge className={`shadow-sm ${getAvailabilityColor(roomType.availableRooms, roomType.totalRooms)}`}>
                    {roomType.availableRooms}/{roomType.totalRooms} available
                  </Badge>
                </div>

                <div className="w-full bg-gray-200 rounded-full h-3 shadow-inner">
                  <div 
                    className="bg-gradient-to-r from-purple-500 to-blue-600 h-3 rounded-full transition-all duration-500 shadow-sm"
                    style={{ 
                      width: `${(roomType.availableRooms / roomType.totalRooms) * 100}%` 
                    }}
                  />
                </div>
              </div>

              {roomType.amenities && roomType.amenities.length > 0 && (
                <div>
                  <span className="text-sm font-medium text-muted-foreground">Amenities:</span>
                  <div className="flex flex-wrap gap-1 mt-1">
                    {roomType.amenities.map((amenity, index) => (
                      <Badge key={index} variant="outline" className="text-xs">
                        {amenity}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}

              {roomType.description && (
                <p className="text-sm text-muted-foreground">
                  {roomType.description}
                </p>
              )}

              <div className="pt-2 border-t">
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-muted-foreground">Type:</span>
                    <p className="font-medium capitalize">
                      {roomType.type.replace('_', ' ')}
                    </p>
                  </div>
                  <div>
                    <span className="text-muted-foreground">Status:</span>
                    <p className={`font-medium ${roomType.availableRooms > 0 ? 'text-green-600' : 'text-red-600'}`}>
                      {roomType.availableRooms > 0 ? 'Available' : 'Fully Booked'}
                    </p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
              ))}

              {roomTypes.length === 0 && (
                <div className="col-span-full">
                  <Card className="border-0 shadow-lg bg-gradient-to-br from-white to-gray-50/30">
                    <CardContent className="flex flex-col items-center justify-center py-16">
                      <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-purple-600 rounded-2xl flex items-center justify-center mb-6">
                        <Home className="h-8 w-8 text-white" />
                      </div>
                      <h3 className="text-xl font-semibold mb-3 text-gray-800">No Room Types Found</h3>
                      <p className="text-gray-600 text-center max-w-md">
                        Room types will be automatically created when you set up your hotel.
                      </p>
                    </CardContent>
                  </Card>
                </div>
              )}
            </div>
          </TabsContent>

          <TabsContent value="qr-codes">
            <RoomQRDisplay hotelId={hotel?.id} />
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
}

client/src/pages/service-requests.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Search, Bell, Clock, CheckCircle, XCircle, AlertCircle } from "lucide-react";
import { ServiceRequest } from "@shared/types";
import ServiceRequestCard from "@/components/service-request-card";

export default function ServiceRequests() {
  const [searchTerm, setSearchTerm] = useState("");

  const { data: serviceRequests = [], isLoading } = useQuery<ServiceRequest[]>({
    queryKey: ["/api/service-requests"],
  });

  const filteredRequests = serviceRequests.filter(request =>
    (request.description ? request.description.toLowerCase().includes(searchTerm.toLowerCase()) : false) ||
    (request.roomNumber ? request.roomNumber.toLowerCase().includes(searchTerm.toLowerCase()) : false) ||
    (request.type ? request.type.toLowerCase().includes(searchTerm.toLowerCase()) : false)
  );

  const pendingRequests = filteredRequests.filter(req => req.status === "pending");
  const assignedRequests = filteredRequests.filter(req => req.status === "assigned");
  const inProgressRequests = filteredRequests.filter(req => req.status === "in_progress");
  const completedRequests = filteredRequests.filter(req => req.status === "completed");

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "pending":
        return <AlertCircle className="w-4 h-4" />;
      case "assigned":
        return <Clock className="w-4 h-4" />;
      case "in_progress":
        return <Clock className="w-4 h-4" />;
      case "completed":
        return <CheckCircle className="w-4 h-4" />;
      case "cancelled":
        return <XCircle className="w-4 h-4" />;
      default:
        return <Bell className="w-4 h-4" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending":
        return "bg-yellow-100 text-yellow-800";
      case "assigned":
        return "bg-blue-100 text-blue-800";
      case "in_progress":
        return "bg-purple-100 text-purple-800";
      case "completed":
        return "bg-green-100 text-green-800";
      case "cancelled":
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-r from-white to-orange-50/50 shadow-sm border-b border-gray-100 px-6 py-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent">Service Requests</h2>
            <p className="text-sm text-gray-600 mt-1">Manage and track all guest service requests</p>
          </div>
          <div className="flex items-center space-x-2">
            <Badge className="bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-sm" data-testid="badge-total-requests">
              Total: {serviceRequests.length}
            </Badge>
            <Badge className="bg-gradient-to-r from-red-500 to-red-600 text-white shadow-sm" data-testid="badge-pending-requests">
              Pending: {pendingRequests.length}
            </Badge>
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="flex-1 overflow-y-auto p-6 bg-gradient-to-br from-gray-50/30 to-orange-50/20">
        {/* Search */}
        <Card className="mb-8 border-0 shadow-lg bg-gradient-to-r from-white to-gray-50/50">
          <CardContent className="p-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search by description, room number, or type..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-requests"
              />
            </div>
          </CardContent>
        </Card>

        {/* Status Tabs */}
        <Tabs defaultValue="pending" className="space-y-8">
          <TabsList className="grid w-full grid-cols-4 bg-white shadow-lg border-0 h-14">
            <TabsTrigger value="pending" className="flex items-center space-x-2 data-[state=active]:bg-gradient-to-r data-[state=active]:from-yellow-500 data-[state=active]:to-yellow-600 data-[state=active]:text-white">
              {getStatusIcon("pending")}
              <span>Pending</span>
              <Badge className="bg-yellow-100 text-yellow-800 shadow-sm">
                {pendingRequests.length}
              </Badge>
            </TabsTrigger>
            <TabsTrigger value="assigned" className="flex items-center space-x-2">
              {getStatusIcon("assigned")}
              <span>Assigned</span>
              <Badge variant="secondary" className={getStatusColor("assigned")}>
                {assignedRequests.length}
              </Badge>
            </TabsTrigger>
            <TabsTrigger value="in_progress" className="flex items-center space-x-2">
              {getStatusIcon("in_progress")}
              <span>In Progress</span>
              <Badge variant="secondary" className={getStatusColor("in_progress")}>
                {inProgressRequests.length}
              </Badge>
            </TabsTrigger>
            <TabsTrigger value="completed" className="flex items-center space-x-2">
              {getStatusIcon("completed")}
              <span>Completed</span>
              <Badge variant="secondary" className={getStatusColor("completed")}>
                {completedRequests.length}
              </Badge>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="pending">
            <Card className="border-0 shadow-lg bg-gradient-to-br from-white to-yellow-50/30">
              <CardHeader className="pb-4">
                <CardTitle className="flex items-center space-x-3 text-xl font-semibold text-gray-800">
                  <div className="w-8 h-8 bg-gradient-to-br from-yellow-500 to-yellow-600 rounded-lg flex items-center justify-center">
                    <AlertCircle className="w-4 h-4 text-white" />
                  </div>
                  <span>Pending Requests</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="text-center py-8">Loading service requests...</div>
                ) : pendingRequests.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    No pending requests
                  </div>
                ) : (
                  <div className="space-y-4">
                    {pendingRequests.map((request) => (
                      <ServiceRequestCard key={request.id} request={request} />
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="assigned">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Clock className="w-5 h-5 text-blue-600" />
                  <span>Assigned Requests</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {assignedRequests.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    No assigned requests
                  </div>
                ) : (
                  <div className="space-y-4">
                    {assignedRequests.map((request) => (
                      <ServiceRequestCard key={request.id} request={request} />
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="in_progress">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Clock className="w-5 h-5 text-purple-600" />
                  <span>In Progress</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {inProgressRequests.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    No requests in progress
                  </div>
                ) : (
                  <div className="space-y-4">
                    {inProgressRequests.map((request) => (
                      <ServiceRequestCard key={request.id} request={request} />
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="completed">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <CheckCircle className="w-5 h-5 text-green-600" />
                  <span>Completed Requests</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {completedRequests.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    No completed requests
                  </div>
                ) : (
                  <div className="space-y-4">
                    {completedRequests.map((request) => (
                      <ServiceRequestCard key={request.id} request={request} />
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
}

